define(['jquery'],function($){return{init:function(){var urlParams=new URLSearchParams(window.location.search);var action=urlParams.get('action');var rownum=urlParams.get('rownum');var userid=urlParams.get('userid');if(action==='grader'&&(rownum!==null||userid!==null)){this.injectPlayer();}},injectPlayer:function(){var $readyLink=$('.s3video-watch-link');if($readyLink.length===0){console.log('S3 Video: No video found on this page');return;}console.log('S3 Video: Found video link, injecting player...');var href=$readyLink.attr('href');var match=href.match(/id=(\d+)/);if(!match){console.error('S3 Video: Could not extract submission ID');return;}var submissionId=match[1];var s3keyMatch=href.match(/s3key=([^&]+)/);if(!s3keyMatch){console.error('S3 Video: Could not extract s3_key');return;}var s3key=decodeURIComponent(s3keyMatch[1]);var containerId='s3video-player-'+submissionId+'-'+Date.now();var playerHtml='<div class="s3video-grading-view"><div class="s3video-player-wrapper"><div class="s3video-player-container" id="'+containerId+'"><div class="s3video-loading"><div class="spinner-border text-light" role="status"><span class="sr-only">Loading video...</span></div><p>Loading video player...</p></div></div></div></div>';$readyLink.parent().html(playerHtml);require(['assignsubmission_s3video/player'],function(Player){console.log('S3 Video: Initializing player with:',{s3key:s3key,submissionId:submissionId,containerId:containerId});Player.init(s3key,submissionId,containerId);});}}});